// Convert Data From Labels and Series into Objects

// -------------- Example of Original Data would look like

// const origDataFormat = {
//     labels: ["M", "T", "W", "T", "F", "S", "S"],
//     series: ["Reed": [12, 17, 7, 17, 23, 18, 28], "Gary": [13, 19, 22, 10, 28, 18, 26], "Clive": [18, 12, 21, 24, 33, 29, 31], "Nicolas": [13, 18, 22, 17, 28, 24, 27]]
// }

export default function dataConverter(origDataFormat) {

    const dataArr = []
    origDataFormat.series.map((item, index) => {
        const seriesSectionArr = [];
        for (var i = 0; i < item.count.length; i++) {
            seriesSectionArr.push({
                [item.name]: item.count[i]
            })
        }
        dataArr.push(seriesSectionArr);
    });

    origDataFormat.labels.map()


    const series = origDataFormat.series;
    const keys = Object.keys(origDataFormat.series);

    console.log(series);
    console.log(keys);

    const sampleData = []

    for (var i = 0; i < keys.length; i++) {
        const dataSection = []
        for (var x = 0; x < series[keys[i]].count.length; x++) {
            dataSection.push([keys[i], series[keys[i]].count[x]]);
        }
        sampleData.push(dataSection)
    }

    const realData = []

    for (var i = 0; i < sampleData[0].length; i++) {
        const obj1 = {"name": origDataFormat.labels[i]}
        for (var x = 0; x < sampleData.length; x++) {
            obj1[sampleData[x][i][0]] = sampleData[x][i][1];
        }
        realData.push(obj1)
    }

    return [realData, keys];
}

