import React from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend, ResponsiveContainer } from 'recharts';

import dataConverter from "services/dataConverter";

// const origDataFormat = {
//     labels: ["M", "T", "W", "T", "F", "S", "S"],
//     series: {"Reed": [12, 17, 7, 17, 23, 18, 28], "Gary": [13, 19, 22, 10, 28, 18, 26], "Clive": [18, 12, 21, 24, 33, 29, 31], "Nicolas": [13, 18, 22, 17, 28, 24, 27]}
// }

export default function LineGraph(props) {
    const colors = ["#fea320", "#932bad", "#eb4946", "#19bcd0"];
    const [realData, keys] = dataConverter(props.data);
    return (
        <ResponsiveContainer width="100%" height={250}>
            <LineChart data={realData}
                margin={{ top: 5, right: 40, left: -40, bottom: 5 }}>
                <CartesianGrid strokeDasharray="3 3" stroke="black" />
                <XAxis dataKey="name" stroke="black" strokeWidth="2"/>
                <YAxis fontSize="10px" stroke="black" strokeWidth="2" />
                <Tooltip />
                <Legend iconSize={20} />
                {keys.map((key, index) => <Line type="linear" dataKey={key} stroke={colors[index]} strokeWidth="3" dot={{strokeWidth: "5"}} />)}
            </LineChart>
        </ResponsiveContainer>
    )
}
