import React from "react";
import { ResponsivePie } from '@nivo/pie'
import { ResponsiveContainer } from "recharts";

// make sure parent container have a defined height when using
// responsive component, otherwise height will be 0 and
// no chart will be rendered.
// website examples showcase many properties,
// you'll often use just a few of them.

const CenteredMetric = ({ dataWithArc, centerX, centerY }) => {
    let total = 0
    dataWithArc.forEach(datum => {
        total += datum.value
    })


    return (
        <text
            x={centerX}
            y={centerY}
            textAnchor="middle"
            dominantBaseline="central"
            style={{
                fontSize: '40px',
                fontWeight: '600',
            }}
        >
            {total}
        </text>
    )
}

export default function CustomPieChart(props) {
    return (
        <ResponsiveContainer width="80%" height={400}>
            <ResponsivePie
                data={props.data}
                layers={['slices', 'sliceLabels', 'radialLabels', 'legends', CenteredMetric]}
                margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                innerRadius={0.55}
                padAngle={0.7}
                cornerRadius={3}
                colors={{ datum: 'data.color' }}
                borderColor={{ from: 'color', modifiers: [['darker', '0']] }}
                radialLabelsSkipAngle={2}
                radialLabelsTextXOffset={5}
                radialLabelsTextColor="#333333"
                radialLabelsLinkOffset={3}
                radialLabelsLinkDiagonalLength={17}
                radialLabelsLinkHorizontalLength={17}
                radialLabelsLinkStrokeWidth={3}
                radialLabelsLinkColor={{ from: 'color', modifiers: [] }}
                sliceLabelsSkipAngle={10}
                sliceLabelsTextColor="white"
                legends={[
                    {
                        anchor: 'bottom',
                        direction: 'row',
                        justify: false,
                        translateX: 0,
                        translateY: 56,
                        itemsSpacing: 0,
                        itemWidth: 70,
                        itemHeight: 18,
                        itemTextColor: '#999',
                        itemDirection: 'left-to-right',
                        itemOpacity: 1,
                        symbolSize: 18,
                        symbolShape: 'circle',
                        effects: [
                            {
                                on: 'hover',
                                style: {
                                    itemTextColor: '#000'
                                }
                            }
                        ]
                    }
                ]}
            />
        </ResponsiveContainer>
    )
}
