import React from "react";

import { Grid, makeStyles } from '@material-ui/core';

import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardIcon from "components/Card/CardIcon.js";
import CardFooter from "components/Card/CardFooter.js";

import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import Update from "@material-ui/icons/Update";
import ErrorOutlineIcon from '@material-ui/icons/ErrorOutline';
import ReceiptIcon from '@material-ui/icons/Receipt';

import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";

const useStyles = makeStyles(styles);

const jsonRES = {
    sales: {
        today: '...',
        week: '...',
        month: '...',
        year: '...'
    },
    totalProducts: '...',
    unshippedOrders: '...'
}

function iconGen(theme) {
    if (theme === "success") {
        return (
            <MonetizationOnIcon />
        )
    } else if (theme === "danger") {
        return (
            <ErrorOutlineIcon />
        )
    } else if (theme === "info") {
        return (
            <ReceiptIcon />
        )
    }
}

function KpiCard(props) {
    const classes = useStyles();
    return (
        <Card>
            <CardHeader stats icon>
                <CardIcon color={props.theme}>
                    {iconGen(props.theme)}
                </CardIcon>
                <p className={classes.cardCategory}>{props.title}</p>
                <h3 className={classes.cardTitle}>{props.content}</h3>
            </CardHeader>
            <CardFooter stats>
                <div className={classes.stats}>
                    <Update />
                    Just Updated
                </div>
            </CardFooter>
        </Card>
    )
}

const moreStyles = makeStyles(() => ({
    grid: {
        display: 'flex',
        flexFlow: 'row wrap'
    }
}))

export default function Kpi1() {
    const classes = moreStyles();
    return (
        <Grid container spacing={3} xs={12} sm={12} md={12} lg={12} style={{ marginBottom: "40px", padding: "30px" }}>
            <Grid item md={4} lg={4} >
                <KpiCard theme="success" title="Today Sales" content={jsonRes.sales.today} />
            </Grid>
            <Grid item md={4} lg={4}>
                <KpiCard theme="success" title="Week to Date Sales" content={jsonRes.sales.today} />
            </Grid>
            <Grid item md={4} lg={4}>
                <KpiCard theme="danger" title="Unshipped Orders" content={jsonRes.unshippedOrders} />
            </Grid>
            <Grid item md={4} lg={4}>
                <KpiCard theme="success" title="Month to Date Sales" content="$8,640.49" />
            </Grid>
            <Grid item md={4} lg={4}>
                <KpiCard theme="success" title="Year to Date Sales" content="$97,064.24" />
            </Grid>
            <Grid item md={4} lg={4}>
                <KpiCard theme="info" title="Total Products" content="20,863" />
            </Grid>
        </Grid>
    )
}
