import React from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend, ResponsiveContainer } from 'recharts';

const origDataFormat = {
    labels: ["M", "T", "W", "T", "F", "S", "S"],
    series: {"Reed": [12, 17, 7, 17, 23, 18, 28], "Gary": [13, 19, 22, 10, 28, 18, 26], "Clive": [18, 12, 21, 24, 33, 29, 31], "Nicolas": [13, 18, 22, 17, 28, 24, 27]}
}

const series = origDataFormat.series;
const keys = Object.keys(origDataFormat.series);

const sampleData = []

for (var i = 0; i < keys.length; i++) {
    const dataSection = []
    for (var x = 0; x < series[keys[i]].length; x++) {
        dataSection.push([keys[i], series[keys[i]][x]]);
    }
    sampleData.push(dataSection)
}

const realData = []

for (var i = 0; i < sampleData[0].length; i++) {
    const obj1 = {"name": origDataFormat.labels[i]}
    for (var x = 0; x < sampleData.length; x++) {
        obj1[sampleData[x][i][0]] = sampleData[x][i][1];
    }
    realData.push(obj1)
}

export default function LineGraph(props) {
    const colors = ["#fea320", "#932bad", "#eb4946", "#19bcd0"]
    return (
        <ResponsiveContainer width="110%" height={250}>
            <LineChart data={realData}
                margin={{ top: 5, right: 60, left: -40, bottom: 5 }}>
                <CartesianGrid strokeDasharray="3 3" stroke="white" />
                <XAxis dataKey="name" stroke="white" strokeWidth="2"/>
                <YAxis fontSize="10px" stroke="white" strokeWidth="2" />
                <Tooltip />
                <Legend iconSize={20} style={{fontSize: "200px"}} />
                {keys.map((key, index) => <Line type="monotone" dataKey={key} stroke={colors[index]} strokeWidth="4" dot={{strokeWidth: "5"}} />)}
            </LineChart>
        </ResponsiveContainer>
    )
}
