import React, { useState, useEffect } from "react";
import axios from "axios";
// react plugin for creating charts
import ChartistGraph from "react-chartist";
// @material-ui/core
import { makeStyles } from "@material-ui/core/styles";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
import Store from "@material-ui/icons/Store";
import Warning from "@material-ui/icons/Warning";
import DateRange from "@material-ui/icons/DateRange";
import LocalOffer from "@material-ui/icons/LocalOffer";
import Update from "@material-ui/icons/Update";
import ArrowUpward from "@material-ui/icons/ArrowUpward";
import AccessTime from "@material-ui/icons/AccessTime";
import Accessibility from "@material-ui/icons/Accessibility";
import BugReport from "@material-ui/icons/BugReport";
import Code from "@material-ui/icons/Code";
import Cloud from "@material-ui/icons/Cloud";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Tasks from "components/Tasks/Tasks.js";
import CustomTabs from "components/CustomTabs/CustomTabs.js";
import Danger from "components/Typography/Danger.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardIcon from "components/Card/CardIcon.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";

import LineGraph from "components/Graphs/LineGraph";
import BarGraph from "components/Graphs/BarGraph";

import AttachMoneyIcon from '@material-ui/icons/AttachMoney';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';

import Kpi1 from "components/KPI/Kpi1";
import Kpi2 from "components/KPI/Kpi2";
import Kpi3 from "components/KPI/Kpi3";
import Kpi4 from "components/KPI/Kpi4";

import dataConverter from "services/dataConverter";

import getMonth from "services/getMonth";

import { bugs, website, server } from "variables/general.js";

import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import { Typography } from "@material-ui/core";

const useStyles = makeStyles(styles);


export default function Dashboard() {
  const classes = useStyles();

  const [kpi1, updateKpi1] = useState([]);
  const [kpi2, updateKpi2] = useState({
    data: [],
    keys: []
  });
  const [kpi3, updateKpi3] = useState([]);
  const [kpi4, updateKpi4] = useState({
    products: {
      today: [],
      week: [],
      month: [],
      year: []
    },
    totalSales: {
      today: [],
      week: [],
      month: [],
      year: []
    },

    // products: [],
    // totalSales: []
  });

  useEffect(() => {

    const currMonth = getMonth();

    const requestOne = axios.get('http://localhost:5000/kpi1/');
    const requestTwo = axios.get(`http://localhost:5000/kpi2/${currMonth}`);
    const requestThree = axios.get('http://localhost:5000/kpi3');
    const requestFour = axios.get('http://localhost:5000/kpi4');

    axios.all([requestOne, requestTwo, requestThree, requestFour])
      .then(axios.spread((...responses) => {
        updateKpi1(responses[0].data);
        updateKpi2({
          data: responses[1].data[0],
          keys: responses[1].data[1]
        });
        updateKpi3(responses[2].data);
        updateKpi4(() => {
          const products = responses[3].data[0];
          const totalSales = responses[3].data[1];
          return {
            products: responses[3].data[0],
            totalSales: responses[3].data[1],
            products: {
              today: products.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.today,
                  "color": datum.color
                }
              }),
              week: products.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.week,
                  "color": datum.color
                }
              }),
              month: products.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.month,
                  "color": datum.color
                }
              }),
              year: products.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.year,
                  "color": datum.color
                }
              })
            },
            totalSales: {
              today: totalSales.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.today,
                  "color": datum.color
                }
              }),
              week: totalSales.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.week,
                  "color": datum.color
                }
              }),
              month: totalSales.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.month,
                  "color": datum.color
                }
              }),
              year: totalSales.map(datum => {
                return {
                  "id": datum.id,
                  "label": datum.label,
                  "value": datum.value.year,
                  "color": datum.color
                }
              })
            }
          }

        });
      }))
      .catch(errors => console.log(errors));

  }, []);

  return (
    <div>
      <Kpi1 kpi1={kpi1} />
      <Kpi4 products={kpi4.products} totalSales={kpi4.totalSales} />
      <GridContainer style={{ marginTop: "50px" }}>
        <GridItem xs={12} sm={12} md={6}>
          <Kpi2 data={kpi2.data} keys={kpi2.keys} />
        </GridItem>
        <GridItem xs={12} sm={12} md={6} style={{ marginTop: "3vh"}}>
          <Kpi3 rows={kpi3} />
        </GridItem>
        {/* <GridItem xs={12} sm={12} md={6}>
          <Card chart>
            <CardHeader color="warning"> */}
              {/* <BarGraph /> */}
            {/* </CardHeader>
            <CardBody>
              <h4 className={classes.cardTitle}>Email Subscriptions</h4>
              <p className={classes.cardCategory}>Last Campaign Performance</p>
            </CardBody>
            <CardFooter chart>
              <div className={classes.stats}>
                <AccessTime /> campaign sent 2 days ago
                </div>
            </CardFooter>
          </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={6}>
          <Card chart>
            <CardHeader color="info"> */}
              {/* <LineGraph data={kpi2} height={250} /> */}
            {/* </CardHeader>
            <CardBody>
              <h4 className={classes.cardTitle}>Completed Tasks</h4>
              <p className={classes.cardCategory}>Last Campaign Performance</p>
            </CardBody>
            <CardFooter chart>
              <div className={classes.stats}>
                <AccessTime /> campaign sent 2 days ago
                </div>
            </CardFooter>
          </Card>
        </GridItem>
      </GridContainer>
      <GridContainer>
        <GridItem xs={12} sm={12} md={6}>
          <CustomTabs
            title="Tasks:"
            headerColor="primary"
            tabs={[
              {
                tabName: "Bugs",
                tabIcon: BugReport,
                tabContent: (
                  <Tasks
                    checkedIndexes={[0, 3]}
                    tasksIndexes={[0, 1, 2, 3]}
                    tasks={bugs}
                  />
                )
              },
              {
                tabName: "Website",
                tabIcon: Code,
                tabContent: (
                  <Tasks
                    checkedIndexes={[0]}
                    tasksIndexes={[0, 1]}
                    tasks={website}
                  />
                )
              },
              {
                tabName: "Server",
                tabIcon: Cloud,
                tabContent: (
                  <Tasks
                    checkedIndexes={[1]}
                    tasksIndexes={[0, 1, 2]}
                    tasks={server}
                  />
                )
              }
            ]}
          />
        </GridItem>
        <GridItem xs={12} sm={12} md={6}>
          <Card>
            <CardHeader color="warning">
              <h4 className={classes.cardTitleWhite}>Employees Stats</h4>
              <p className={classes.cardCategoryWhite}>
                New employees on 15th September, 2016
                </p>
            </CardHeader>
            <CardBody>
              <Table
                tableHeaderColor="warning"
                tableHead={["ID", "Name", "Salary", "Country"]}
                tableData={[
                  ["1", "Dakota Rice", "$36,738", "Niger"],
                  ["2", "Minerva Hooper", "$23,789", "CuraÃ§ao"],
                  ["3", "Sage Rodriguez", "$56,142", "Netherlands"],
                  ["4", "Philip Chaney", "$38,735", "Korea, South"]
                ]}
              />
            </CardBody>
          </Card>
        </GridItem> */}
      </GridContainer>
    </div>
  );
}
